<template>
	<v-app dark>
		<v-toolbar app>
			<v-toolbar-title class="headline text-uppercase">
				<span>To DO</span>
			</v-toolbar-title>
		</v-toolbar>

		<v-content>
			<v-container>
				<v-layout text-xs-center wrap>
					<v-flex xs12>
						<v-img
							src="data:image/svg+xml;utf8;base64,"
							class="my-3" contain height="200"></v-img>
					</v-flex>
				</v-layout>
			</v-container>

			<progress-task :progress="progress"></progress-task>
			<new-task @taskAdded="addTask"></new-task>
			<task-grid :tasks="tasks" @taskDeleted="taskRemove" @taskStateChanged="toggleTaskState"/>

		</v-content>
	</v-app>
</template>

<script>
import ProgressTask from './components/ProgressTask.vue';
	import NewTask from './components/NewTask.vue';
	import TaskGrid from "./components/TaskGrid";

	export default {
		name: "App",
		components: {
			TaskGrid,
			NewTask,
			ProgressTask
		},
		data() {
			return {
				tasks: []
			};
		},
		computed: {
			progress() {
				const total = this.tasks.length
				const done = this.tasks.filter(t => !t.pending).length

				return Math.round(done / total * 100) || 0
			}
		},
		created () {
			 const json = localStorage.getItem('tasks')
			 this.tasks = JSON.parse(json) || []
		},
		methods: {
			addTask(task) {
				const sameName = t => t.name === task.name
				const reallyNew = this.tasks.filter(sameName).length == 0
				if(reallyNew){
					this.tasks.push({
						name: task.name,
						pending: task.pending || true
					})
				}				
			},
			taskRemove(index) {
				this.tasks.splice(index, 1);
			},
			toggleTaskState(i) {
				this.tasks[i].pending = !this.tasks[i].pending 
			}
		},
		watch: {
			tasks: {
				deep: true,
				handler() {
					localStorage.setItem('tasks', JSON.stringify(this.tasks))
				},
			}
		},
	};
</script>
